import React, { useState } from 'react';
import styles from '../CSS/login.module.css';
import logo from '../Components/logo.png';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    setErrorMessage(''); // Clear previous error messages

    try {
      const response = await fetch('http://localhost:3000/api/auth/login', { // Change URL as needed
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        // Assuming the server responds with a plain text error message
        const errorMsg = await response.text();
        throw new Error(errorMsg || 'Failed to login. Please check your email and password.');
      }

      // Handle successful login here
      // If your backend sets a session cookie, there's nothing more to do here
      // If you need to redirect the user to another page:
      // window.location.href = '/dashboard'; // Redirect to dashboard or appropriate page

      console.log('Login successful!'); // For debugging
      // Redirect or perform further actions based on the response
    } catch (error) {
      setErrorMessage(error.message || 'Something went wrong!');
    }
  };

  return (
    <div className={styles.container}>
      <form className={styles.form} onSubmit={handleSubmit}>
        <img src={logo} alt="Company Logo" className={styles.logo} />
        <h3 className={styles.heading}>Login</h3>
        <p className={styles.welcomeBack}>Welcome back! Please login to your account.</p>

        {errorMessage && <p className={styles.error}>{errorMessage}</p>}

        <div className={styles.inputGroup}>
          <label htmlFor='email'>Email</label>
          <input
            type='email'
            id='email'
            placeholder='Enter your Email'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className={styles.inputGroup}>
          <label htmlFor='password'>Password</label>
          <input
            type='password'
            id='password'
            placeholder='Enter your Password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className={styles.forgotPassword}>
          <a href='/forgot-password'>Forgot password?</a>
        </div>
        <button className={styles.loginButton} type="submit">
          Login
        </button>
        <div className={styles.registerLink}>
          If you don't have an account <a href='/signup'>Sign up</a>
        </div>
      </form>
    </div>
  );
}

export default LoginPage;
