import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../Components/nav-admin'; // Ensure the path is correct

function UserTable() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:3002/user/view');
            setUsers(response.data);
        } catch (error) {
            console.error('Failed to fetch users:', error);
        }
    };

    const toggleVerify = async (userId) => {
        try {
            const response = await axios.patch(`http://localhost:3002/verify/${userId}`);
            if (response.data) {
                setUsers(prevUsers =>
                    prevUsers.map(user =>
                        user.id === userId ? { ...user, verified: !user.verified } : user
                    )
                );
            } else {
                throw new Error('Verification toggle failed at the server');
            }
        } catch (error) {
            console.error('Failed to verify user:', error);
            // Optionally, revert the verify status if needed based on specific requirements
        }
    };

    const handleDeleteUser = async (userId) => {
        try {
            const response = await axios.delete(`http://localhost:3002/user/delete/${userId}`);
            if (response.status === 200) {
                setUsers(prevUsers => prevUsers.filter(user => user.id !== userId));
            } else {
                throw new Error('Failed to delete the user');
            }
        } catch (error) {
            console.error('Failed to delete user:', error);
        }
    };

    return (
        <div className="user-management-container">
            <Navbar />
            <div className="user-table-container">
                <table className="user-table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Actions</th>
                            <th>Verify</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user) => (
                            <tr key={user.id}>
                                <td>{user.userame}</td>
                                <td>{user.email}</td>
                                <td>{user.role}</td>
                                <td>
                                    <button onClick={() => alert("Edit functionality not yet implemented")}>Edit</button>
                                    <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
                                </td>
                                <td>
                                    <button onClick={() => toggleVerify(user.id)}>
                                        {user.verified ? 'Verified' : 'Verify'}
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default UserTable;
