// Import necessary hooks and libraries
import React, { useState } from 'react';
import axios from 'axios';  // Axios for HTTP requests
import {jwtDecode } from 'jwt-decode';  // To decode JWT token
import styles from '../CSS/login.module.css';  // Import CSS module for styling
import logo from '../Components/logo.png';  // Logo import

function LoginPage({ setToken }) {
    const [email, setEmail] = useState('');  // State for email input
    const [password, setPassword] = useState('');  // State for password input
    const [error, setError] = useState('');  // State to handle any login error

    const handleSubmit = async (event) => {
        event.preventDefault();  // Prevent the form from refreshing the page
        try {
            // Post request to backend login route
            const { data } = await axios.post('http://localhost:3001/login', { email, password });
            if (data.token) {
                const user = jwtDecode(data.token);  // Decode token to get user data
                console.log('User logged in:', user);  // Optional: log the user data
                setToken(data.token);  // Set the token in App's state or context
            } else {
                throw new Error('Token not provided!');
            }
        } catch (err) {
            // Handle errors with either response error or generic error
            setError(err.response?.data || 'Failed to login');
        }
    };

    return (
        <div className={styles.container}>
            <form className={styles.form} onSubmit={handleSubmit}>
                <div>
                    <img src={logo} alt="Company Logo" className={styles.logo} style={{height: '120px', width: '250px'}}/>
                    <h3 className={styles.heading}>Login</h3>
                    <p style={{fontSize: '21px', textAlign: 'center'}}>Welcome back! Please login to your account.</p>
                    <div className={styles.inputGroup}>
                        <label htmlFor='email'>Email</label>
                        <input
                            type='email'
                            id='email'
                            placeholder='Enter your Email'
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className={styles.inputGroup}>
                        <label htmlFor='password'>Password</label>
                        <input
                            type='password'
                            id='password'
                            placeholder='Enter your Password'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    {error && <p className={styles.error}>{error}</p>} {/* Display login error if any */}
                    <div className={styles.forgotPassword}>
                        <a href='/FP'>Forgot password?</a>
                    </div>
                    <button className={styles.loginButton} type="submit">Login</button>
                    <p className={styles.forgotPassword}>If you don't have an account <a href='/signup'>Sign up</a></p>
                    </div>
                </form>
            </div>
    );
}

export default LoginPage;
