import React, { useState } from 'react';
import Navbar from '../Components/nav-admin'; // Ensure the path is correct

function UserTable() {
    const [data, setData] = useState({
        users: [
            { id: 1, name: "Janujah Sivarathinam", email: "janujahsivarattinam@gmail", role: "admin" },
            { id: 2, name: "", email: "", role: "" },
            { id: 3, name: "", email: "", role: "" }
        ],
        doctors: [
            { id: 1, name: "",email:"", specialization: "" },
            { id: 2, name: "", specialization: "" }
        ],
        technicians: [
            { id: 1, name: "",email:"", field: "" },
            { id: 2, name: "", email:"",field: "" }
        ],
        consultations: [
            { id: 1, date: "2024-01-01", doctorId: 1, userId: 2 },
            { id: 2, date: "2024-01-02", doctorId: 2, userId: 3 }
        ],
        orders: [
            { id: 1, product: "Medicine A", quantity: 10 },
            { id: 2, product: "Medicine B", quantity: 20 }
        ],
        payments: [
            { id: 1, amount: 100.00 },
            { id: 2, amount: 200.00 }
        ]
    });

    const handleDeleteUser = (id) => {
        setData(prevData => ({
            ...prevData,
            users: prevData.users.filter(user => user.id !== id)
        }));
    };
    const toggleVerify = (userId) => {
        setData(prevData => ({
            ...prevData,
            users: prevData.users.map(user => 
                user.id === userId ? { ...user, verified: !user.verified } : user
            )
        }));
    };


    return (
        <div className="user-management-container">
            <Navbar/>
            <div style={{display:'flex', justifyContent:'space-between'}}>
                <div className="user-count-card">
                    <h3>Users: {data.users.length}</h3>
                </div>
                <div className="user-count-card">
                    <h3>Doctors: {data.doctors.length}</h3>
                </div>
                <div className="user-count-card">
                    <h3>Technicians: {data.technicians.length}</h3>
                </div>
                <div className="user-count-card">
                    <h3>Consultations: {data.consultations.length}</h3>
                </div>
                <div className="user-count-card">
                    <h3>Orders: {data.orders.length}</h3>
                </div>
                <div className="user-count-card">
                    <h3>Payments: {data.payments.length}</h3>
                </div>
            </div>
            <div className="user-table-container">
            return (
    <div className="user-management-container">
        <Navbar/>
        <div style={{display:'flex', justifyContent:'space-between'}}>
            {/* Statistics cards... */}
        </div>
        <div className="user-table-container">
            <table className="user-table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Actions</th>
                        <th>Verify</th> {/* Additional column for verify */}
                    </tr>
                </thead>
                <tbody>
                    {data.users.map((user) => (
                        <tr key={user.id}>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.role}</td>
                            <td>
                                <button onClick={() => alert("Go, and write the function for this.")}>Edit</button>
                                <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
                            </td>
                            <td>
                                <button onClick={() => toggleVerify(user.id)}>
                                    {user.verified ? 'Verified' : 'Verify'}
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    </div>
);

            </div>
        </div>
    );
}

export default UserTable;
