import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

function ProfilePage() {
    const [profileImage, setProfileImage] = useState(null);
    const [signatureImage, setSignatureImage] = useState(null);

    const formik = useFormik({
        initialValues: {
            fullName: '',
            email: '',
            bio: '',
            availableTime: '',
            registeredId: '',
            workingHospitals: '',
            age: '',
            contactNo: ''
        },
        validationSchema: Yup.object({
            fullName: Yup.string().required('Full name is required'),
            email: Yup.string().email('Invalid email address').required('Email is required'),
            bio: Yup.string(),
            availableTime: Yup.string(),
            registeredId: Yup.string().required('Registered ID is required'),
            workingHospitals: Yup.string().required('Current working hospitals are required'),
            age: Yup.number().positive().integer().required('Age is required'),
            contactNo: Yup.string().matches(/^[0-9]{10}$/, 'Must be a valid 10-digit phone number').required('Contact number is required')
        }),
        onSubmit: (values) => {
            const formData = new FormData();
            Object.keys(values).forEach(key => formData.append(key, values[key]));
            if (profileImage) formData.append('profileImage', profileImage);
            if (signatureImage) formData.append('signatureImage', signatureImage);

            axios.post('http://localhost:3002/Doctors/create', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            })
            .then(response => {
                alert('Profile updated successfully!');
            })
            .catch(error => {
                alert('Failed to update profile.');
                console.error('Error:', error);
            });
        },
    });

    const handleImageChange = (event, setImage) => {
        const file = event.currentTarget.files[0];
        if (file) {
            setImage(file);
            const fileReader = new FileReader();
            fileReader.onloadend = () => {
                formik.setFieldValue(event.currentTarget.name, fileReader.result);
            };
            fileReader.readAsDataURL(file);
        }
    };

    return (
        <div className="profile-container" style={{ padding: "20px", fontFamily: "Arial" }}>
            <h1 style={{ textAlign: "center" }}>Your Profile</h1>
            <form onSubmit={formik.handleSubmit} style={{ display: "flex", flexDirection: "column", maxWidth: "500px", margin: "auto" }} encType="multipart/form-data">

                {/* Form fields dynamically generated based on initialValues */}
                {Object.keys(formik.initialValues).map((key) => (
                    key !== 'profileImage' && key !== 'signatureImage' ? (
                        <div key={key}>
                            <label htmlFor={key}>{key.charAt(0).toUpperCase() + key.slice(1).replace(/([A-Z])/g, ' $1').trim()}:</label>
                            <input
                                id={key}
                                type={key === 'age' || key === 'contactNo' ? 'number' : 'text'}
                                {...formik.getFieldProps(key)}
                                style={{ padding: "8px", margin: "5px 0" }}
                            />
                            {formik.touched[key] && formik.errors[key] && <div style={{ color: "red" }}>{formik.errors[key]}</div>}
                        </div>
                    ) : null
                ))}

                {/* Profile Picture */}
                <label htmlFor="profileImage">Profile Picture:</label>
                <input
                    id="profileImage"
                    name="profileImage"
                    type="file"
                    accept="image/*"
                    onChange={(event) => handleImageChange(event, setProfileImage)}
                    style={{ margin: "5px 0" }}
                />
                {profileImage && <img src={URL.createObjectURL(profileImage)} alt="Profile" style={{ width: "100px", height: "100px", marginBottom: "10px" }} />}

                {/* Digital Signature */}
                <label htmlFor="signatureImage">Digital Signature:</label>
                <input
                    id="signatureImage"
                    name="signatureImage"
                    type="file"
                    accept="image/*"
                    onChange={(event) => handleImageChange(event, setSignatureImage)}
                    style={{ margin: "5px 0" }}
                />
                {signatureImage && <img src={URL.createObjectURL(signatureImage)} alt="Signature" style={{ width: "100px", height: "100px", marginBottom: "10px" }} />}

                <button type="submit" style={{ padding: "10px", background: "#4CAF50", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" }}>
                    Save Profile
                </button>
            </form>
        </div>
    );
}

export default ProfilePage;
