import React, { useState } from 'react';
import '../CSS/contactUs.css'

function ContactForm() {


    const [result, setResult] = React.useState("");


  const onSubmit = async (event) => {
    event.preventDefault();
    setResult("Sending....");
    const formData = new FormData(event.target);

    formData.append("access_key", "YOUR_ACCESS_KEY_HERE");

    const response = await fetch("https://api.web3forms.com/submit", {
      method: "POST",
      body: formData
    });

    const data = await response.json();

    if (data.success) {
      setResult("Form Submitted Successfully");
      event.target.reset();
    } else {
      console.log("Error", data);
      setResult(data.message);
    }
  };


    return (
        <form onSubmit={onSubmit} className='form'>
            <div>
                <img src="https://img.freepik.com/free-vector/flat-design-illustration-customer-support_23-2148887720.jpg?t=st=1714574452~exp=1714578052~hmac=fcd3d2293312c7a50ed65766306df081c799b0a5329960f33fe4305396da9084" style={{height:'555px'}}></img>
            </div>
            <div style={{marginTop:'96px'}}>
            <div>
                <label htmlFor="name">Name:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label htmlFor="email">Email:</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label htmlFor="message" >Message:</label>
                <textarea
                    id="message"
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    style={{height:'100px'}}
                    required
                />
            </div>
            <button type="submit" className='button'>Send</button>
            </div>

        </form>
    );
}

export default ContactForm;
