import React, { useState, useEffect } from 'react';
import Navbar from '../Components/nav-admin';

function UserTable() {
    const [users, setUsers] = useState([]);
    const [editFormData, setEditFormData] = useState(null);  // Holds the data of the user being edited

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        fetch('http://localhost:3002/Doctors/view')
            .then(response => response.json())
            .then(data => setUsers(data))
            .catch(error => {
                console.error('Failed to fetch users:', error);
                alert('Failed to load users.');
            });
    };

    const verifyUser = (id) => {
        if (!id) {
            alert('User ID is missing.');
            return;
        }

        fetch(`http://localhost:3002/Doctors/verify/${id}`, {
            method: 'PATCH',
            headers: { 'Content-Type': 'application/json' },
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(() => {
                alert('User verified successfully');
                fetchUsers();  // Refresh the users list
            })
            .catch(error => {
                console.error('Error verifying user:', error);
                alert(`Failed to verify the user: ${error.message}`);
            });
    };

    const deleteUser = (id) => {
        if (!id) {
            alert('User ID is missing.');
            return;
        }

        if (window.confirm('Are you sure you want to delete this user?')) {
            fetch(`http://localhost:3002/Doctors/delete/${id}`, {
                method: 'DELETE',
            })
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    return response.json();
                })
                .then(data => {
                    alert(data.message || 'User deleted successfully');
                    fetchUsers();  // Optionally update all data or modify locally
                })
                .catch(error => {
                    console.error('Error:', error);
                    alert('Failed to delete the user.');
                });
        }
    };

    // Open modal with user data
    const openEditModal = (user) => {
        setEditFormData(user);
    };

    // Close modal
    const closeEditModal = () => {
        setEditFormData(null);
    };

    // Handle form changes
    const handleEditFormChange = (event) => {
        const { name, value } = event.target;
        setEditFormData(prevData => ({
            ...prevData,
            [name]: value,
        }));
    };

    // Submit edit form
    const submitEditForm = (id) => {
        if (!id || !id) {
            alert('Error: No user data to update.');
            return;
        }

        fetch(`http://localhost:3002/Doctors/update/${id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(editFormData),
        })
            .then(response => response.json())
            .then(() => {
                alert('User updated successfully');
                fetchUsers();
                closeEditModal();
            })
            .catch(error => {
                console.error('Error updating user:', error);
                alert('Failed to update user.');
            });
    };

    return (
        <div className="user-management-container">
            <Navbar />
            {editFormData && (
                <div className="edit-modal">
                    <form onSubmit={e => { e.preventDefault(); submitEditForm(); }}>
                        <label>Name</label>
                        <input
                            type="text"
                            name="userName"
                            value={editFormData.userName || ''}
                            onChange={handleEditFormChange}
                        />
                        {/* Add more fields as necessary */}
                        <button type="submit">Save Changes</button>
                        <button onClick={closeEditModal}>Cancel</button>
                    </form>
                </div>
            )}
            <div className="user-table-container">
                <table className="user-table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Registered ID</th>
                            <th>Working Hospitals</th>
                            <th>Age</th>
                            <th>Contact No</th>
                            <th>Bio</th>
                            <th>Profile Image</th>
                            <th>Signature Image</th>
                            <th>Actions</th>
                            <th>Verification</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user._id}>
                                <td>{user.userName}</td>
                                <td>{user.email}</td>
                                <td>{user.role}</td>
                                <td>{user.registeredId}</td>
                                <td>{user.workingHospitals}</td>
                                <td>{user.age}</td>
                                <td>{user.contactNo}</td>
                                <td>{user.bio}</td>
                                <td>
                                    <img src={user.profileImage} alt="Profile" style={{ width: "50px", height: "50px" }} />
                                </td>
                                <td>
                                    <img src={user.signatureImage} alt="Signature" style={{ width: "50px", height: "50px" }} />
                                </td>
                                <td>
                                    <button onClick={() => openEditModal(user)}>Edit</button>
                                    <button onClick={() => deleteUser(user._id)}>Delete</button>
                                </td>
                                <td>
                                    {user.isVerified ? 'Verified' : <button onClick={() => verifyUser(user._id)}>Verify</button>}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default UserTable;
