import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../Components/nav-admin'; 

function UserTable() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        fetchUsers();
    }, []);

    // Function to fetch users
    function fetchUsers() {
        axios.get('http://localhost:3002/SignUp/view')
            .then(response => setUsers(response.data))
            .catch(error => console.error('Failed to fetch users:', error));
    }

    // Function to delete users
    function deleteUsers(id) {
        axios.delete(`http://localhost:3002/SignUp/delete/${id}`)
            .then(() => {
                // Filter out the user from the list of users
                setUsers(prevUsers => prevUsers.filter(user => user.id !== id));
                console.log('User deleted successfully');
            })
            .catch(error => {
                console.error('Error deleting user:', error);
            });
    }

    return (
        <div className="user-management-container">
            <Navbar />
            <div className="user-table-container">
                <table className="user-table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Actions</th>
                            <th>Verify</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td>{user.userName}</td>
                                <td>{user.email}</td>
                                <td>{user.Role}</td>
                                <td>
                                    <button onClick={() => alert("Edit functionality not yet implemented")}>Edit</button>
                                    <button onClick={() => deleteUsers(user.id)}>Delete</button>
                                </td>
                                <td>
                                    <button onClick={() => alert("Verify functionality not yet implemented")}>
                                        {user.verified ? 'Verified' : 'Verify'}
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default UserTable;
