// src/components/UserTable.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../Components/nav-admin';

function UserTable() {
    const [users, setUsers] = useState([]);
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        axios.get('http://localhost:3002/SignUp/view')
            .then(response => setUsers(response.data))
            .catch(error => console.error('Failed to fetch users:', error));
    }

    const deleteUsers = () => {
        // // function deleteUser(id) {
        //     fetch(`http://localhost:3000/SignUp/delete/${id}`, {
        //         method: 'DELETE',  // Specify the method to use
        //         headers: {
        //             'Content-Type': 'application/json'  // Ensure appropriate headers are set
        //         }
        //     })
        //     .then(response => {
        //         if (response.ok) {
        //             return response.json();  // Process the response if it's OK
        //         }
        //         throw new Error('Something went wrong on api server!');  // Handle errors
        //     })
        //     .then(data => {
        //         console.log('User deleted successfully:', data);
        //         // You might want to update state here to remove the user from the list
        //     })
        //     .catch(error => {
        //         console.error('Error deleting user:', error);
        //     });
        // }

        // const handleDelete = () => {
            const url = new URL(`http://localhost:3000/SignUp/delete`);

            url.searchParams.append('email', email); 

            fetch(url, {
                method: 'DELETE'
            })
            .then(response => {
                if (response.ok) {
                    return response.text();
                }
                throw new Error('Failed to delete the user');
            })
            .then(data => {
                alert(data); 
            })
            .catch(error => {
                console.error('Error:', error);
                alert(error.message); 
            });
        };



    return (
        <div className="user-management-container">
            <Navbar />
            <div className="user-table-container">
                <table className="user-table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td>{user.userName}</td>
                                <td>{user.email}</td>
                                <td>{user.Role}</td>
                                <td>
                                    <button onClick={() => alert('Edit functionality not implemented')}>Edit</button>
                                    <button onClick={() => deleteUsers(user.id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default UserTable;
