import React, { useState } from 'react';
import styles from '../CSS/login.module.css';
import logo from '../Components/logo.png';
import {jwtDecode} from 'jwt-decode'; // Corrected import statement for jwt-decode

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Call the API to authenticate the user
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });

      if (response.ok) {
        const { token } = await response.json();
        const decoded = jwtDecode(token);
        console.log('User authenticated:', decoded);
        // You can set user data to global state/store or local/session storage here
      } else {
        throw new Error('Failed to login. Please check your credentials.');
      }
    } catch (error) {
      setErrorMessage(error.message);
    }
  }

  return (
    <div className={styles.container}>
      <form className={styles.form} onSubmit={handleSubmit}>
        <img src={logo} alt="Company Logo" className={styles.logo} />
        <h3 className={styles.heading}>Login</h3>
        <p className={styles.welcomeBack}>Welcome back! Please login to your account.</p>

        {errorMessage && <p className={styles.error}>{errorMessage}</p>}

        <div className={styles.inputGroup}>
          <label htmlFor='email'>Email</label>
          <input
            type='email'
            id='email'
            placeholder='Enter your Email'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className={styles.inputGroup}>
          <label htmlFor='password'>Password</label>
          <input
            type='password'
            id='password'
            placeholder='Enter your Password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className={styles.forgotPassword}>
          <a href='/forgot-password'>Forgot password?</a>
        </div>
        <button className={styles.loginButton} type="submit">
          Login
        </button>
        <div className={styles.registerLink}>
          If you don't have an account <a href='/signup'>Sign up</a>
        </div>
      </form>
    </div>
  );
}

export default LoginPage;
