import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../Components/nav-admin'; 

function UserTable() {
    const [users, setUsers] = useState([]);
    const [counts, setCounts] = useState({
        users: 0,
        doctors: 0,
        technicians: 0,
        consultations: 0,
        orders: 0,
        payments: 0
    });

    // useEffect (() => {
    //     axios.get('http://localhost:3002/SignUp/view')
    //     .then(account=> setUsers(account.data))
    //     .catch (err=>console.log(err))
    // },[])
    useEffect(() => {
        fetchUsers();
        fetchCounts();
    }, []);

    const fetchCounts = async () => {
        try {
            const response = axios.get('http://localhost:3002/user/view'); 
            setCounts(response.data);
        } catch (error) {
            console.error('Failed to fetch counts:', error);
        }
    };


    const fetchUsers = async () => {
        try {
            axios.get('http://localhost:3002/users/view')
            .then(response => setUsers(response.data));
        } catch (err){
            console.log(err)
        }
    };

    const toggleVerify = async (email) => {
        try {
            const response = await axios.patch(`http://localhost:3002/verify/${email}`);
            if (response.data) {
                setUsers(prevUsers =>
                    prevUsers.map(user =>
                        user.id === email ? { ...user, verified: !user.verified } : user
                    )
                );
            } else {
                throw new Error('Verification toggle failed at the server');
            }
        } catch (error) {
            console.error('Failed to verify user:', error);
        }
    };

    const handleDeleteUser = async (email) => {
        try {
            const response = await axios.delete(`http://localhost:3002/user/delete/${email}`);
            if (response.status === 200) {
                setUsers(prevUsers => prevUsers.filter(user => user.id !== email));
            } else {
                throw new Error('Failed to delete the user');
            }
        } catch (error) {
            console.error('Failed to delete user:', error);
        }
    };

    return (
        <div className="user-management-container">
            <Navbar />
            <div style={{display: 'flex', justifyContent: 'space-between', flexWrap: 'wrap'}}>
            <div className="user-count-card">
                <h3>Users: {counts.users}</h3>
            </div>
            <div className="user-count-card">
                <h3>Doctors: {counts.doctors}</h3>
            </div>
            <div className="user-count-card">
                <h3>Technicians: {counts.technicians}</h3>
            </div>
            <div className="user-count-card">
                <h3>Consultations: {counts.consultations}</h3>
            </div>
            <div className="user-count-card">
                <h3>Orders: {counts.orders}</h3>
            </div>
            <div className="user-count-card">
                <h3>Payments: {counts.payments}</h3>
            </div>
        </div>

        <div className="user-table-container">
                <table className="user-table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Actions</th>
                            <th>Verify</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user) => (
                            <tr key={user.id}>
                                <td>{user.userName}</td>
                                <td>{user.email}</td>
                                <td>{user.Role}</td>
                                <td>
                                    <button onClick={() => alert("Edit functionality not yet implemented")}>Edit</button>
                                    <button onClick={() => alert("Edit functionality not yet implemented")}>Delete</button>
                                </td>
                                <td>
                                    <button onClick={() => alert("Edit functionality not yet implemented")}>
                                        {user.verified ? 'Verified' : 'Verify'}
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default UserTable;
