import React, { useState, useEffect } from 'react';
import DoctorCard from './doccard';
import Nav from './navbar';

function DoctorPage() {
    const [doctors, setDoctors] = useState([]);
    const isAdmin = true; // This should be determined by your authentication system

    useEffect(() => {
        const url = isAdmin ? 'http://localhost:3002/Doctors/view-all' : 'http://localhost:3002/Doctors/view';
        fetch(url)
            .then(response => response.json())
            .then(data => setDoctors(data))
            .catch(error => console.error('Error:', error));
    }, [isAdmin]);

    const handleVerify = (id) => {
        fetch(`http://localhost:3002/Doctors/verify/${id}`, {
            method: 'PATCH'
        })
        .then(response => response.json())
        .then(() => {
            setDoctors(doctors.map(doc => {
                if (doc._id === id) {
                    return { ...doc, isVerified: true };
                }
                return doc;
            }));
        })
        .catch(error => console.error('Error verifying doctor:', error));
    };

    return (
        <div>
            <Nav /><br/><br/><br/><br/><br/><br/><br/>
            <div><h1 style={{fontSize:'90px', textAlign:'center'}} className='ourservices'><b>Doctors</b></h1></div><br/>
            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
                {doctors.map(doctor => (
                    <div key={doctor._id}>
                        <DoctorCard doctor={doctor} />
                        {isAdmin && !doctor.isVerified && (
                            <button onClick={() => handleVerify(doctor._id)}>Verify</button>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default DoctorPage;
