import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import styles from '../CSS/login.module.css';
import logo from '../Components/logo.png';

function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
      e.preventDefault();

      try {
          const response = await axios.post('http://localhost:3002/login', { username, password });
          console.log(response.data); // Handle successful login response
      } catch (error) {
          console.error('Login failed:', error.response.data.message);
      }
  };

  return (
    <div className={styles.container}>
      <form className={styles.form} onSubmit={handleSubmit}>
        <img src={logo} alt="Company Logo" className={styles.logo} />
        <h3 className={styles.heading}>Login</h3>
        <p className={styles.welcomeBack}>Welcome back! Please login to your account.</p>

        {error && <div className={styles.error}>{error}</div>}

        <div className={styles.inputGroup}>
          <label htmlFor='email'>Email</label>
          <input
            type='email'
            id='email'
            placeholder='Enter your Email'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className={styles.inputGroup}>
          <label htmlFor='password'>Password</label>
          <input
            type='password'
            id='password'
            placeholder='Enter your Password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className={styles.forgotPassword}>
          <a href='/forgot-password'>Forgot password?</a>
        </div>
        <button className={styles.loginButton} type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
        <div className={styles.registerLink}>
          If you don't have an account <a href='/signup' style={{color:'#0e0737', textDecoration:'none'}}>Sign up</a>
        </div>
      </form>
    </div>
  );
}

export default LoginPage;
