import React, { useRef } from 'react';
import '../CSS/About.css'
import ServiceCard from '../Components/card'

function Ourrservices() {
    const scrollRef = useRef(null);

    const scroll = (scrollOffset) => {
        scrollRef.current.scrollLeft += scrollOffset;
    };

    return (
        <div>
            <button onClick={() => scroll(-200)}>Prev</button> {/* Scroll left */}
            <div ref={scrollRef} className="scrolling-container">
                <ServiceCard
                    imageUrl="https://via.placeholder.com/300x180"
                    title="Web Development"
                    description="Professional web development services to build your online presence."
                />
                <ServiceCard
                    imageUrl="https://via.placeholder.com/300x180"
                    title="Digital Marketing"
                    description="Comprehensive digital marketing solutions to enhance your brand's visibility."
                />
        </div>
        </div>
    );
}

export default ourrservices
